import React, { useState } from 'react';
import s from './VideoTutorials.module.css';
import { useLocation } from 'react-router-dom';
import { isMobile } from 'constants/useMediaQueries';

const Video = ({ el }) => {
  const [modalOpen, setModalOpen] = useState(false);
  const { pathname } = useLocation();
  const mobileScreen = isMobile();
  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <>
      <div className={s.videoItemWrapper} onClick={openModal}>
        <div
          style={{
            justifyContent: 'center',
            display: 'flex',
            width: '100%',
            height: '100%',
            overflow: 'hidden',
            flexDirection: 'column',
            alignItems: 'center',
            borderRadius: '16px',
            marginBottom: '8px',
          }}
        >
          <img
            className={s.imageOfVideo}
            alt="Video Thumbnail"
            src={el.snippet.thumbnails.high.url}
            style={{
              maxWidth: `${pathname === '/video-tips' ? '100%' : 'none'}`,
            }}
            //  height={290}
          />
        </div>
        <h3 className={s.videoTitle}>{el.snippet.title}</h3>

        <p className={s.videoDescription}>
          {mobileScreen
            ? (
                el.snippet.localized?.description || el.snippet.description
              ).split('.')[0]
            : el.snippet.localized?.description || el.snippet.description}
        </p>
      </div>
      {modalOpen && (
        <div
          className="modal"
          onClick={closeModal}
          style={{ display: modalOpen ? 'block' : 'none' }}
        >
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <iframe
              width="100%"
              height="315"
              src={`https://www.youtube.com/embed/${el.id.videoId}`}
              frameBorder="0"
              allowFullScreen
              title="YouTube Video"
            ></iframe>
          </div>
        </div>
      )}
    </>
  );
};

export default Video;
